--
-- Daten für Tabelle `tbl_lesson`
--

INSERT INTO `tbl_lesson` (`id`, `category_id`, `title`, `text`, `img_url`, `gif_url`, `gif_url_static`, `video_url`, `top_lesson`, `lesson_nr`, `important`, `einfuerung`, `lernziele`, `groeste_probleme`) VALUES
(1, 1, 'Energie - Einführung', 'Als erneuerbare oder grüne Energie bezeichnet man Strom, Wärme und Treibstoffe, die wir aus der Sonne, dem Wind, der Wasserkraft oder bestimmten Pflanzen gewinnen können. Sie haben den grossen Vorteil, dass sie sich meist wieder auf natürliche Weise erneuern und wir sie wiederverwenden können. Die Sonne kann man schließlich nicht ausknipsen und der Wind hört nicht plötzlich auf zu wehen. Ausserdem erzeugen erneuerbare Energien in der Regel bei ihrer Nutzung kaum Abfälle und Schadstoffe. Ihre Nutzung stört somit die kommenden Generationen nicht. <br><br>\r\nDer grosse Nachteil von erneuerbaren Energien ist, dass diese im Vergleich zu den herkömmlichen Stromerzeugungsanlagen erheblich teurer sind. Des weiteren sind einige Technologien, vor allem Windkraft, stark von Wetter- und Witterungsbedingungen abhängig.<br><br>\r\nDie herkömmliche Energie wie Öl, Kohle und Erdgas wird auch “fossile Energie” genannt. Aus Öl kann man zum Beispiel Treibstoffe wie Benzin für Autos herstellen; Kohle und Erdgas kann man verbrennen und damit heizen oder Strom gewinnen. Diese Art von Energie ist allerdings schlecht für die Umwelt und trägt einen grossen Beitrag zum Klimawandel bei. Wenn immer möglich, ist deshalb fossile Energie zu vermeiden.<br><br>\r\nWie die einzelnen erneuerbaren Energien funktionieren, was du zu unserer Umwelt beitragen kannst und wie man am besten mit Energie umgeht, findest du in den nachfolgenden Lektionen. Viel Spass!', 'assets/img/energy/lesson01_1.jpg, assets/img/energy/lesson01_2.jpg, assets/img/energy/lesson01_3.jpg,  ', 'assets/gif/energie_01.gif', 'assets/gif/energie_01.jpg', 'assets/video/energy.mp4', 0, 0, '', 1, 'Was versteht man unter erneuerbarer Energie?, Was sind die Vor- und Nachteile von erneuerbarer Energie?, Wie kann man durch Sonne, Wind und Wasser Energie gewinnen?, Was ist der Treibhauseffekt?, Wie kann ich Energie sparen?, ', 'Herkömmliche Energie ist schlecht für die Umwelt, Erneuerbare Energie ist teuer, Die Schweiz ist internationalen Vergleich an den hinteren Positionen, '),
(2, 1, 'Sonnenenergie', 'Die vielleicht bekannteste Quelle erneuerbarer Energie ist die Sonne – und das aus gutem Grund. Immerhin trifft jährlich so viel Solarenergie auf die Erde, dass sie den Energiebedarf der Menschheit gleich um mehrere Tausendfach übersteigt. Bestimmt hast du schon Hausdächer gesehen, auf denen große verspiegelte Scheiben montiert sind. Das sind Solar-Kollektoren, die die Sonnenstrahlen einfangen. Mit der Wärme kann man Wasser erhitzen, das dann durch die Heizung strömt und das Haus heizt. <br><br>\r\nDoch es gibt auch Anlagen, mit denen man Strom produzieren kann. Man nennt sie \"Fotovoltaik-Anlagen\". Sie sind häufig auf Schulen, Fabrikdächern oder großen Scheunen installiert und speisen von dort Energie ins Stromnetz ein.<br><br>\r\nDas Problem dabei ist, die Sonnenstunden in der Schweiz sind begrenzt, denn bei uns ist es ziemlich oft bewölkt und es regnet häufig. Deshalb kann man die Sonnenenergie nicht so gut nutzen wie zum Beispiel die Wind- oder Wasserkraft, welche du in den nächsten Lektionen kennenlernen wirst.', 'assets/img/energy/lesson02_1.jpg, assets/img/energy/lesson02_2.jpg, assets/img/energy/lesson02_3.jpg,  ', 'assets/gif/energie_01.gif', 'assets/gif/energie_01.jpg', '', 0, 1, 'Sonnenenergie kann den Energiebedarf der Welt bei weitem abdecken, Solar-Kollektoren heizen das Haus, Fotovoltaik-Anlagen erzeugen durch Sonnenenergie Strom, Sonnenstunden sind begrenzt, ', 0, '', ''),
(3, 1, 'Wasserkraft', 'Schon im alten Ägypten und im römischen Reich wurde die Wasserkraft als Antrieb für Arbeitsmaschinen wie Getreidemühlen genutzt. Im Mittelalter wurden Wassermühlen im europäischen Raum für Säge- und Papierwerke eingesetzt. Seit Ende des 19. Jahrhunderts wird aus Wasserkraft Strom erzeugt. Heute ist die Wasserkraft eine ausgereifte Technologie und weltweit eine der am meisten genutzte erneuerbare Energiequelle.<br><br>\r\nUm aus Wasser Energie gewinnen zu können gibt es drei gängige Arten von Wasserkraftwerken:\r\n<ul>\r\n\r\n<li>Bei Wasserspeicherkraftwerken wird ein fließendes Gewässer aufgestaut. Ein Stausee entsteht. Dieses Wasser wird durch Rohre abgelassen, in denen sich Turbinen befinden. Diese Turbinen erzeugen die erneuerbare Energie.</li>\r\n<li>Laufwasserkraftwerke nutzen wiederum unmittelbar die Fließenergie von Flüssen, besitzen dafür keine Speicherkapazität. </li>\r\n<li>Meeresströmungskraftwerke, deren Rotoren sich unter Wasser befinden, wandeln die Meeresströmung in Ökostrom um.</li>\r\n</ul>\r\nDer Aufwand so Energie zu gewinnen ist allerdings hoch: Man muss Stauseen, Staustufen und Wehre bauen, damit man die Kraft des Wassers kontrollieren kann. Dies ist nebst dem Aufwand auch immer ein eingriff in die Natur und verändert das Aussehen der Landschaft erheblich.', 'assets/img/energy/lesson03_1.jpg, assets/img/energy/lesson03_2.jpg, assets/img/energy/lesson03_3.jpg,  ', 'assets/gif/energie_02.gif', 'assets/gif/energie_02.jpg', '', 1, 2, 'Wasserkraft gibt es seit dem römischen Reich, Weltweit eine der am meisten genutzte Energiequelle, Aufwand zur Energiegewinnung ist hoch, Eingriff in die Landschaft, ', 0, '', ''),
(4, 1, 'Windenergie', 'Die Nutzung des Windes als Antriebsenergie hat eine lange Tradition. Windmühlen wurden zum Mahlen von Getreide oder als Sägemühle eingesetzt. Moderne Windenergieanlagen gewinnen Strom aus der Kraft des Windes. Sie nutzen den Auftrieb, den der Wind beim Vorbeiströmen an den Rotorblättern erzeugt und treiben dadurch einen Generator an. Dort wird die Windenergie, die die großen Rotoren zuvor aufgenommen haben, in Strom umgewandelt. Die Windräder stehen meist in flachen Landschaften oder sogar im Meer, da es an solchen stellen meist sehr Windig ist.<br><br>\r\nAber auch wenn die Windkraft gut für unser Klima ist, muss beim Bau der Anlagen auch der Schutz der Natur berücksichtigt werden. Nicht überall dürfen die Windräder aufgestellt werden. In Naturschutzgebieten zum Beispiel sind Windkraft-Anlagen nicht erlaubt, da sie dem Wald und seinen Bewohnern schaden könnten.', 'assets/img/energy/lesson01_1.jpg, assets/img/energy/lesson01_2.jpg, assets/img/energy/lesson01_3.jpg,  ', 'assets/gif/energie_03.gif', 'assets/gif/energie_03.jpg', '', 1, 3, 'Windenergie hat eine lange Tradition, Durch das Vorbeiströmen von Wind gewinnen Windräder strom, Windräder stehen in flachen Landschaften oder im Meer, Beim Bau ist der Naturschutz sehr wichtig, ', 0, '', ''),
(5, 1, 'Treibhauseffekt', 'Bei der Verbrennung von Öl, Kohle oder Gas entsteht das Gas Kohlendioxid (CO²), das zu den sogenannten Treibhausgasen gehört. Treibhausgase sind Gase in der Atmosphäre, die die Wärmestrahlung der Erde aufnehmen können und wieder an die Erde zurückstrahlen. Sie sorgen dafür, dass es auf der Erde nicht zu kalt wird. Das ist der Treibhauseffekt. Bis zu einem gewissen Grad ist der Treibhauseffekt absolut notwendig für das Leben auf der Erde,  wie wir es kennen. Die durchschnittliche Temperatur an der Erdoberfläche beträgt +15° Celsius. Ohne den natürlichen Treibhauseffekt läge sie bei -18° Celsius. <br><br>\r\nWenn allerdings zuviele Treibhausgase entstehen, wird es auf der Erde immer wärmer und das Wetter spielt verrückt. Man nennt diese Veränderungen \"Klimawandel\".<br><br>\r\nErneuerbare Energien tragen entscheidend zur Verringerung klimaschädlicher Treibhausgase bei und damit auch zum stoppen des Klimawandels.', 'assets/img/energy/lesson01_1.jpg, assets/img/energy/lesson01_2.jpg, assets/img/energy/lesson01_3.jpg,  ', 'assets/gif/energie_04.gif', 'assets/gif/energie_04.jpg', '', 0, 4, 'Bei der Verbrennung entsteht CO², Treibhausgase sorgen für wärme auf der Erde, Zu viel Treibhausgase treiben den Klimawandel voran, Erneuerbare Energien helfen den Klimawandel zu stoppen, ', 0, '', ''),
(6, 1, 'Mein Energieverhalten ', 'Wir haben durch unser Verhalten in unseren vier Wänden einen wichtigen Einfluss auf den Energieverbrauch. Jedes Mal, wenn wir die Heizung andrehen, den Wasserkocher einschalten oder uns einen Film anschauen, wird Strom und Energie benötigt. Da der meiste Strom nach wie vor aus fossilen Energieträgern wie Kohle und Atomkraftwerken gewonnen wird, tragen wir mit unserem Stromverbrauch zur Freisetzung von Kohlendioxid bei.<br><br>\r\nMit den folgenden Tipps kannst du dein Energieverhalten verbessern: \r\n<ul>\r\n<li>Licht muss nur brennen, wenn es nötig ist. </li>\r\n<li>Sind elektrische Geräte nicht im Betrieb, können diese komplett aus der Steckdose gezogen werden.</li>\r\n<li>Wasserkocher statt Herdplatte brauchen.</li>\r\n<li>Energiesparlampen oder LED Lampen verwenden.</li>\r\n<li>Neue Geräte sind meist Energiesparender als die Alten.</li>\r\n<li>Geräte Aus- statt in Stand-By-Modus schalten.</li>\r\n<li>Anstatt Google, Ecosia.org verwenden.</li>\r\n<li>Stosslüften statt Fenster auf kipp stellen.</li>\r\n<li>Bei Spül- oder Waschmaschine das Stromsparprogramm verwenden.</li>\r\n</ul>\r\nJeder ist verantwortlich für die Umwelt und kann zum Klimaschutz beitragen. Wenn alle nachhaltig mitwirken kann Großes geschaffen werden!', 'assets/img/energy/lesson01_1.jpg, assets/img/energy/lesson01_2.jpg, assets/img/energy/lesson01_3.jpg,  ', 'assets/gif/energie_05.gif', 'assets/gif/energie_05.jpg', '', 0, 5, 'Wir haben durch unser Verhalten Einfluss auf den Klimawandel, Bewusst Energie konsumieren, Durch weniger Energieverbrauch können kosten gespart werden, ', 0, '', ''),
(7, 2, 'Nachhaltigkeit - Einführung', 'In diesem Kapitel werden vier verschiedene Themenbereiche behandelt. In der ersten Lektion schauen wir uns an, was der Faire Handel ist und warum er so wichtig in der heutigen Zeit ist. Danach gehen wir auf die Nachhaltige Bewirtschaftung ein. Hier lernen wir, was die Nachhaltige Bewirtschaftung überhaupt ist und mit welchen Problemen wir konfrontiert werden. In der dritten Lektion geht es um ein ziemlich kompliziertes Thema, um die Ressourceneffizienz. Keine Angst, wenn du dir darunter noch nicht vorstellen kannst, dies wird dir in der Lektion erklärt. In der letzten Lektion geht es um die Förderung unserer lokalen Produkte. Warum ist das wichtig? Und was für einen Einfluss hat unser Kaufverhalten auf die Umwelt? All das und noch einiges mehr, wirst du hier erfahren.', '', 'assets/gif/konsum_01.gif', 'assets/gif/konsum_01.jpg', '', 0, 0, '', 1, 'Wie funktioniert Fairer Handel?, Warum kann Fairer Handel zu Nachhaltigkeit beitragen?, Wie sieht nachhaltige Bewirtschaftung aus?, Wie werden natürliche Ressourcen in der Schweiz genutzt?, Warum müssen wir die lokalen Produkte fördern?, ', 'Viele Kleinbauer und Arbeiter in Entwicklungsländer werden noch immer oft ausgenutzt., Oft werden Frauen in Entwicklungsländer sehr schlechte Arbeitsbedingungen und verdienen nur wenig Geld damit., Der weite Transport der Produkte schadet der Umwelt., '),
(8, 2, 'Fairer Handel', '<b>Was ist Fairtrade?</b><br>\r\nFairtrade ist die internationale Bezeichnung für, den Fairen Handel von Produkten. Es ist ein internationales System, welches die Produzenten stärkt und faire Arbeitsbedingungen fördert. Damit dies funktionieren kann, muss jeder Akteur, der beteiligt ist, eingebunden werden.<br><br>\r\nBeim Fairen Handel geht es nicht nur um den nachhaltigen Anbau, sondern auch um den späteren Handel mit dem Produkt und dem Verhalten der Konsumentinnen und Konsumenten.<br><br>\r\n<b>Warum ist Fairer Handel wichtig?</b><br>\r\nIn vielen Teilen der Welt, ist der allgemeine Handel oft sehr ausbeuterisch. Kleinbauern und Arbeiter werden ausgenutzt und eingeschüchtert. Zudem müssen sie mit schlechten Arbeitsbedingungen und niedrigen Löhnen versuchen auszukommen. Auch Frauen arbeiten in verschiedenen Ländern oft zwölf Stunden am Tag und werden dafür sehr schlecht bezahlt. Durch diese Bedingungen verarmen viele Familien und haben dadurch nicht das nötige Geld, ihre Kinder in die Schule schicken zu können. Die Kinder haben jedoch nicht einfach frei, sondern Sie müssen Arbeiten gehen, um die Familie finanziell zu unterstützen.<br><br>\r\nAuch die Umwelt leidet unter diesem Vorgehen. Da viele Arbeiten in Entwicklungsländer verrichtet werden, findet ein reger Transport der erarbeiteten Materialien statt.<br><br>\r\nDurch den Fairen Handel wird nun aber dagegen vorgegangen. Fairer Handel bedeutet, dass Menschen, die etwas verkaufen auch fair für ihre Arbeit bezahlt werden und dies unter sicheren und gesunden Arbeitsbedingungen. Dafür bekommen die lokalen Arbeiter eine Fairhandelsprämie, mit der sie z.B. Schulen bauen können. Zudem ist die Kinderarbeit im Fairen Handel ausnahmslos verboten und auch die Umwelt wird bestmöglich geschützt.', '', 'assets/gif/konsum_01.gif', 'assets/gif/konsum_01.jpg', '', 1, 1, 'Fairer Handel bedeutet, dass die Produzenten fair behandelt und bezahlt werden., Kinderarbeit ist beim Fairen Handel ausnahmslos verboten., Die Umwelt wird durch den Fairen Handel bestmöglich geschützt., Achte bei Einkaufen auf das Siegel für Fairen Handel, ', 0, '', ''),
(9, 2, 'Nachhaltige Bewirtschaftung', 'Für das Leben sind die Ressourcen auf der Welt ausschlaggebend. Es gibt zwei entscheidende Faktoren; das Wasser und die Luft. Oft wird aber vergessen was unter unseren Füssen ist. Nämlich der Boden. Er ist die Hauptquelle unserer Ernährung, die Grundlage für unsere Häuser und Strassen aber auch der Lebensraum von Millionen von Arten. Auf ihm wachsen Gräser, Blumen und ganze Wälder. Diese bilden den Lebensraum vieler Säugetiere. Sie haben aber auch eine andere Aufgabe, denn sie tragen einen grossen Teil zur Regulierung des Klimas bei.<br><br>\r\nIn der Forstwirtschaft bedeutet dies nun, es darf nicht mehr Holz gefällt werden, als jeweils Bäume nachwachsen können.<br><br>\r\n<b>Welche Herausforderungen gibt es?</b><br>\r\nDurch die zunehmende Nutzung der Wälder durch den Menschen, schwinden diese kontinuierlich. Sie werden durch unterschiedliche Aktivitäten genutzt, wie z.B. den Bau von Verkehrsnetzen und die Zersiedlung.<br><br>\r\nFür uns ist es also wichtig, dass die Wälder bestehen bleiben und wir mit Rücksicht damit umgehen.', '', 'assets/gif/konsum_02.gif', 'assets/gif/konsum_02.jpg', '', 0, 2, 'Wälder können helfen, unsere Atemluft rein zu halten., Wir dürfen nicht mehr Bäume fällen, als gleichzeitig nachwachsen können., Rücksicht gegenüber unserer Bestehenden Natur ist das A und O., ', 0, '', ''),
(10, 2, 'Effiziente Nutzung natürlicher Ressourcen', 'Unter «Effizienter Nutzung natürlicher Ressourcen» oder auch «Ressourceneffizienz», versteht man das Verhältnis eines bestimmten Nutzens gegenüber des erforderlichen Einsatzes an natürlichen Ressourcen. Der nutzen kann dabei in Form eines Produktes oder auch einer Dienstleistung erbracht werden. Daraus können wir schliessen, dass, je geringer der dafür nötige Input an natürlichen Ressourcen ist oder je höher der Nutzen des Produktes oder der Dienstleistung ist, desto höher ist schliesslich auch die Ressourceneffizienz.<br><br>\r\nUm diesen Sachverhalt zu verstehen, müssen wir zuerst Wissen, was Ressourcen sind.<br><br>\r\n<b>Ressourcen</b><br>\r\nRessourcen sind die Mittel, die zur Produktion von Produkten oder Dienstleistungen benötigt werden. Beispiele für natürliche Ressourcen sind; Luft, Wasser oder auch die Bodenfläche.<br><br>\r\n<b>Beispiel</b>\r\nWir dürfen nicht mehr Wälder abholzen als diese nachwachsen können. Dadurch ist es umso wichtiger, dass wir diese erneuerbaren Ressource so effizient wie möglich Nutzen.', '', 'assets/gif/konsum_03.gif', 'assets/gif/konsum_03.jpg', '', 0, 3, 'Die Waage zwischen dem Nutzen der durch ein Produkt oder eine Dienstleistung gewonnen wird und der natürlichen Ressourcen darf nie in Schräglage geraten., Wir müssen die bestehenden natürlichen Ressourcen so effizient als möglich nutzen und dürfen nichts verschwenden., ', 0, '', ''),
(11, 2, 'Förderung lokaler Produkte und Kultur', 'Besonders regionale Geschäfte haben es oft sehr schwer, sich auf dem Markt durchsetzen zu können. Grosse globale Händler haben oft ein grösseres Sortiment und mehr Möglichkeiten ihre Kunden zu erreichen.<br><br>\r\nDennoch gibt es einige gute Gründe um in einem regionalen Geschäft einkaufen zu gehen.\r\n<ol>\r\n<li>Umweltschonend: Die Produkte kommen aus der Region und verursachen weniger CO2 als eingeflogene Produkte.</li>\r\n<li>Ökonomischer nutzen: Dadurch, dass man regional einkauft, wird automatisch auch das Bruttoinlandprodukt gefördert.</li>\r\n<li>Qualität: Bei uns in der Schweiz gibt es sehr hohe Qualitätsstandards und du weist genau, woher deine Produkte stammen.</li>\r\n<li>Preis: Oft ist es so, dass regionale Produkte sogar noch Billiger sind als Importprodukte.</li>\r\n<li>Gesünder: Weil die regionalen Produkte ausschliesslich Reif geerntet werden, enthalten diese mehr Vital- und Nährstoffe.</li>\r\n</ol>', '', 'assets/gif/konsum_04.gif', 'assets/gif/konsum_04.jpg', '', 0, 4, 'Regional einkaufen bedeutet saisonal einkaufen, Saisonal einkaufen bedeutet aber nicht gleichzeitig regional einkaufen, Kleine lokale Geschäfte haben oft keine Chance gegen grosse Globale Händler, ', 0, '', ''),
(12, 4, 'Food Waste - Einführung', 'Also nochmal: Im Schnitt geht jedes dritte Lebensmittel zwischen Feld und Teller verloren oder wird verschwendet. Diese Lebensmittelverschwendung hat weitreichende Auswirkungen auf Klima und Umwelt, ist also nicht gut.<br><br>\r\n\r\nWas Food Waste ist, wieso wir Lebensmittel verschwenden und was die Lebensmittelverschwendung mit der Umwelt und dem Klima zu tun hat, erklären wir dir in dieser Lektion.', '', 'assets/gif/food-waste_01.gif', 'assets/gif/food-waste_01.jpg', '', 0, 0, '', 1, 'Was versteht man unter Food-Waste?, Wo entsteht Food Waste in der Schweiz?, Welche Auswirkungen hat Food Waste auf das Klima?, Wie können wir Food-Waste verhindern?, ', ''),
(13, 4, 'Was ist Food Waste?', 'Unter Food Waste versteht man alle Lebensmittel, die für den menschlichen Konsum produziert wurden und auf dem Weg vom Feld bis zum Teller verloren gehen oder weggeworfen werden. Food Waste entsteht auf allen Stufen der Lebensmittelherstellung, in der Landwirtschaft, weil sich die Lebensmittel aufgrund von Normanforderungen nicht für den Verkauf eignen, oder im Restaurant, wenn Tellerreste oder Buffetüberschüsse entsorgt werden, oder auch in den Haushalten, wenn wir zum Beispiel den letzten Schluck in der Flasche nicht mehr trinken.<br><br>\r\n\r\nDoch nicht alles was wir wegwerfen zählt zu Food Waste. Wenn man eine Karotte schält und die Schale wegwirft obwohl sie einwandfrei geniessbar ist, handelt es sich um Food Waste.  Ungeniessbare Teile wie Rüstabfälle, Knochen oder Kaffeesatz hingegen sind nicht essbar und zählen deshalb nicht zum Food Waste. <br><br>\r\n\r\nWir unterscheiden also zwischen vermeidbaren und unvermeidbaren Lebensmittelabfällen. Vermeidbare Lebensmittelabfälle umfassen alle Lebensmittel und Lebensmittelteile, welche essbar und gesundheitlich einwandfrei sind, aber wegen Verderb, Verfall oder Qualitätsmängeln nicht von Menschen verzehrt werden. <br><br>\r\n\r\nUnvermeidbare Lebensmittelabfälle umfassen alle nicht essbaren Teile von Lebensmitteln wie Rüstabfälle, Käserinden und Abfälle, welche bei bestem Wissen und Gewissen nicht vermeidbar sind. (z.B. Kartoffeln, welche trotz korrekter Lagerbedingungen von einem Krankheitserreger befallen werden)', '', 'assets/gif/food-waste_01.gif', 'assets/gif/food-waste_01.jpg', '', 0, 1, 'Food Waste entsteht auf allen Stufen der Lebensmittelherstellung, Unvermeidbare Lebensmittelabfälle zählen nicht zu Food Waste, ', 0, '', ''),
(14, 4, 'Food Waste in der Schweiz', 'In der Schweiz geht über die gesamte Lebensmittelkette circa ein Drittel aller Lebensmittel verloren. Auch weltweit liegt die Zahl in diesem Bereich, trotzdem gibt es Unterschiede. In reicheren Ländern wie der Schweiz, wird ein Grossteil von Endkonsumenten weggeworfen, da wir uns dies schlicht leisten können. In ärmeren Ländern landet in den Haushalten verglichen sehr wenig im Abfall. In diesen Regionen der Welt sind dafür die Verluste bei der Ernte, in der Lagerung und der Verarbeitung höher, da effiziente Technologien und das nötige Know-How fehlen.<br><br>\r\n\r\nIn der Schweiz entsteht am meisten Food Waste, rund 35% der gesamten Lebensmittelabfälle, in der Verarbeitung. Dies geschieht zum Beispiel weil nicht alle Nebenprodukte verwendet werden. (z.B. Molke, Tofu, Teigreste oder Innereien)<br><br>\r\n\r\nAuch in den Haushalten entstehen extrem viele Lebensmittelabfälle, 28%, zusammen mit der Gastronomie 35%. In diesen Bereichen entsteht Food Waste weil wir zu viel einkaufen, Lebensmittel vergessen oder nicht korrekt lagern und weil wir Resten nicht verwerten.<br><br>\r\n\r\nIn der Landwirtschaft entstehen 20% aller Lebensmittelverluste, da die Ernte aus technischen Gründen nie voll eingefahren werden kann und nicht normgerechte Produkte aussortiert werden müssen. (z.B. missförmige Karotten, zu grosse Zucchetti, oder zu kleine Kartoffeln)<br><br>\r\n\r\nDiese ganze Verschwendung bringt auch finanzielle Folgen. Ein Schweizer Haushalt gibt durchschnittlich 7% des Jahreseinkommens für Lebensmittel aus. Nach einer Erhebung des Bundesamtes für Statistik (BFS) sind das etwa 636 Franken im Monat. Gleichzeitig werfen wir pro Person und Jahr in der Schweiz 620 Franken weg für Lebensmittel, die wir zwar gekauft, aber nicht gegessen haben. Unter dem Strich könnten wir also jedes Jahr noch einen Monat länger Lebensmittel einkaufen.\r\n', '', 'assets/gif/food-waste_02.gif', 'assets/gif/food-waste_02.jpg', '', 1, 2, 'In der Schweiz wird ca. ein Drittel aller Lebensmittel verloren, Am meisten Food Waste entsteht in der Schweiz in der Verarbeitung, Durchschnittlich geben wir CHF 636.00 pro Monat Pro Person für Lebensmittel aus, Im Jahr verursacht eine Person Food Waste im Wert von ca. CHF 620.00, ', 0, '', ''),
(15, 4, 'Auswirkung auf das Klima', 'Food Waste wirkt sich auch auf unsere Umwelt und das Klima aus. In der Schweiz verursachen Lebensmittelabfälle so viel Umweltbelastung wie die Hälfte aller Autofahrten. Die Umweltbelastung eines Lebensmittel wird stärker, je länger der Weg ist. Je weiter hinten in der Lebensmittelherstellungskette Lebensmittel also entsorgt werden, desto schlimmer. Es macht also einen grossen Unterschied ob ein Bauer eine Karotte kompostiert oder ein Konsument eine Avocado wegwirft.<br><br>\r\n\r\nWir verschwenden über alle Stufen der Lebensmittelkette hinweg jedes dritte Lebensmittel.\r\nIn den Wertschöpfungsketten von Broten und Backwaren, Fisch, Lager- und Frischgemüse sowie Kartoffeln geht dabei ein besonders hoher Anteil verloren. Nicht jedes Lebensmittel, welches wir entsorgen, belastet die Umwelt gleich stark. Gerade Lebensmittel zu deren Herstellung viele Ressourcen (Wasser, Boden, Verpackung, Transport) nötig sind, haben einen grösseren Einfluss auf die Umwelt. <br><br>\r\n\r\nDer Umweltbericht 2018 des Bundesrates zeigt auf, dass unsere Ernährung einer der Faktoren ist, welcher die Umwelt am stärksten beeinflussen. Darum ist die Vermeidung von Food Waste ein sehr wichtiges Thema.\r\n', '', 'assets/gif/food-waste_03.gif', 'assets/gif/food-waste_03.jpg', '', 0, 3, 'Die Umweltbelastung von Food Waste ist so viel wie die Hälfte aller Autofahrten, Je weiter hinten in der Lebensmittelherstellungskette Lebensmittel also entsorgt werden desto schlimmer, Lebensmittel zu deren Herstellung viele Ressourcen nötig sind haben einen grösseren Einfluss auf die Umwelt, ', 0, '', ''),
(16, 4, 'Was wir tun können', 'Natürlich sind für die Vermeidung von Food Waste alle Akteure der Lebensmittelherstellungskette gefordert; von den Bäuerinnen und Bauern, zum Detailhandel bis zu den Konsumentinnen und Konsumenten. Alle Akteure können ihren persönlichen Beitrag leisten, um Food Waste zu verringern und jeder kann im eigenen Haushalt dazu beitragen, dass keine Lebensmittel verschwendet werden. Hier zeigen wir dir einfache Massnahmen, die du ergreifen kannst, damit bei dir nichts schlecht wird oder vergessen geht.<br><br>\r\n\r\nClever einkaufen und vor allem nur so viel du brauchst. Erstelle Einkaufslisten, kaufe saisonal und lokal ein, kaufe Aktionen auch nur wenn du sie konsumieren wirst und gehe nie hungrig einkaufen, denn das verleitet dich dazu mehr zu kaufen.<br><br>\r\n\r\nVerlängere die Haltbarkeit deiner Lebensmittel durch optimale Lagerung. Orientiere dich an den Daten auf den Produkten und an deinen Sinnen: sehen, riechen und schmecken, um herauszufinden ob Lebensmittel noch essbar sind. Platziere Essensreste so, dass du sie nicht vergisst und konsumiere sie innerhalb von 1 bis 3 Tagen. Wenn du trotzdem zu viel eingekauft hast, kannst du das meiste eingefrieren.<br><br>\r\n\r\nKoche und serviere kleinere Mengen und portioniere so richtig. Um Resten zu vermeiden solltest du bereits vor dem Kochen überlegen und richtig portionieren. Serviere kleinere Portionen & lagere Resten korrekt im Kühlschrank, um sie später zu konsumieren.<br><br>\r\n\r\nWeitere Tipps:\r\n<ol>\r\n\r\n<li>Auch unförmigem Obst und Gemüse eine Chance geben</li>\r\n<li>Brot und Backwaren vom Vortag kaufen</li>\r\n<li>Auch weniger edle Fleischstücke kaufen</li>\r\n<li>Im Restaurant kleinere Portionen bestellen und Resten einpacken lassen</li>\r\n<li>Aktiv den Dialog zu den Produzentinnen und Produzenten suchen und uns für eine Veränderung stark machen.</li>\r\n</ol>\r\n\r\nFalls trotz all dem Food Waste entsteht, sollten diese Lebensmittelabfälle nicht im Abwasser oder im Kehricht entsorgt werden. Das Entsorgen im Kehricht ist schlecht, denn die Verbrennung von Nährstoffen ist nicht sinnvoll und Produkte mit hohem Wassergehalt verschlechtern die Energieausbeute der Kehrichtverbrennungsanlagen. Lebensmittelabfälle sollten auch nicht dem Abwasser zugeführt werden. Wenn ausnahmsweise Lebensmittelabfälle entsorgt werden müssen, dann nach Möglichkeit im Grüngutcontainer oder im heimischen Kompost.<br><br>\r\nQuelle: https://foodwaste.ch/', '', 'assets/gif/food-waste_04.gif', 'assets/gif/food-waste_04.jpg', '', 0, 4, 'Clever einkaufen und vor allem nur so viel du brauchst, Verlängere die Haltbarkeit deiner Lebensmittel durch optimale Lagerung, Koche und serviere kleinere Mengen und portioniere so richtig, Lebensmittelabfälle nur im Grüngutcontainer oder im heimischen Kompost entsorgen, ', 0, '', ''),
(17, 3, 'Klimawandel - Einführung', '', '', 'assets/gif/klima_01.gif', 'assets/gif/klima_01.jpg', 'assets/video/intro_klima.mp4', 0, 0, '', 1, 'Was ist der Klimawandel und was passiert dabei?, Was sind mögliche Ursachen und Folgen des Klimawandels?, Was kann ich dagegen tun?, ', ''),
(18, 3, 'Klimawandel kurz erklärt', '<b>Was ist der Klimawandel?</b><br>\r\nKlimawandel, Klimaveränderung, Klimaänderung oder Klimawechsel. Du hast sicher schon einmal von einem dieser Begriffe gehört oder gelesen. Sie alle bezeichnen dasselbe Phänomen, nämlich die weltweite Veränderung des Klimas auf der Erde. Im Unterschied zum Wetter, was einen kurzfristigen Zustand, wie Regen oder Schnee, an einem bestimmten Ort zu einem bestimmten Zeitpunkt oder kurzen Zeitraum bezeichnet, betrachtet man beim Klima das Wetter über einen längeren Zeitraum und in einer grösseren Region. <br><br>\r\nDoch wieso ist dieser Begriff seit einiger Zeit so ein Thema? Veränderungen des Klimas, also Abkühlungen oder Erwärmungen, gibt es schon seit Anbeginn, man siehe zum Beispiel die Eiszeit.<br><br>\r\nAllerdings vollziehen sich die Veränderungen der Temperatur normalerweise sehr langsam über einen grossen Zeitraum. Doch zurzeit hat es die Erde mit einer von Menschen gemachten globalen Erwärmung zu tun. Diese geht im Vergleich zu den früheren Wandeln sehr schnell vonstatten, was katastrophale Folgen für die Umwelt, Tiere und Menschen nach sich zieht. <br><br>\r\nIn den letzten 100 Jahren haben Forscher einen Anstieg der Durchschnittstemperatur von ungefähr 0,8°C gemessen. Man rechnet damit, dass ohne Klimaschutz-Massnahmen, die Temperatur bis 2100 um etwa 4-5 °C steigt. Das hört sich erst einmal nach wenig an, aber schon ab einer Veränderung von +2°C wird es zu gravierenden Auswirkungen kommen, wie Jahrhundertfluten, Dürren und gigantischen Wirbelstürmen. <br><br>\r\nIm Vergleich: Bisher erwärmte sich die Erde bei natürlichen Klimaveränderungen (ohne den menschlichen Einfluss) innerhalb von 10.000 Jahren um diese 4-5 °C. Das heisst, momentan vollzieht sich die Klimaveränderung also etwa 100-mal schneller. <br><br>\r\n\r\n<b>Was verursacht den Klimawandel?</b><br>\r\nUrsache für die global Erwärmung sind Treibhausgase. Die Erde wird von einer unsichtbaren Lufthülle umgeben, der Atmosphäre. Die Atmosphäre ist eine hundert Kilometer dicke Schicht aus Luft, die sich vollständig um die Erde zieht. Wenn die Sonne auf die Erde scheint, treffen die Strahlen die Erdoberfläche und erwärmen diese. Diese Strahlen werden von der Erde in die Atmosphäre zurückgeworfen. Ein Grossteil der Strahlung gelangt zurück ins Weltall. Ein Teil davon wird aber durch die sich in der Atmosphäre befindenden Treibhausgase (wie z.B. Wasserdampf, Kohlenstoffdioxid und Methan) aufgehalten. Dieser Teil wird wieder zurück zur Erde reflektiert und die Erde wird so erneut erwärmt. Diese Erwärmung durch die zurückgeschickten Strahlen bezeichnet man als den natürlichen Treibhauseffekt. Dieser natürliche Prozess ist sehr wichtig, damit auf der Erde überhaupt eine angenehm warme Temperatur herrscht.<br><br>\r\nAber die Konzentration der Treibhaugase ist durch den Menschen stark gestiegen. Deshalb bleiben immer mehr Strahlen in der Atmosphäre hängen und werden wieder zurück auf die Erde reflektiert, wodurch sich die Erde immer weiter erwärmt. Diese Verstärkung des Treibhauseffektes durch den Menschen wird auch der “anthropogene Treibhauseffekt” genannt. \r\n', '', 'assets/gif/klima_01.gif', 'assets/gif/klima_01.jpg', '', 0, 1, 'Der Begriff “Klima” bezeichnet die Betrachtung des Wetters über einen längeren Zeitraum (mind. 30 Jahre) in einem bestimmten Gebiet, Schon ab Veränderungen von +2°C kann es zu gravierenden Auswirkungen in der Natur kommen, Der Treibhauseffekt bezeichnet den Vorgang der Erderwärmung durch die mehrmalige Reflektion der Sonnenstrahlen mithilfe der Treibhausgase in der Erdatmosphäre, ', 0, '', ''),
(19, 3, 'Ursachen', '<b>Wie trägt der Mensch zur Problematik des Klimawandels bei? </b><br>\r\nNeben den natürlichen Ursachen für den Klimawandel hat dieser viele menschengemachte Gründe. Insbesondere Kohlenstoffdioxid (CO2) und Methan spielen dabei eine grosse Rolle. Über die Hälfte des vom Menschen verursachten Treibhauseffektes entfallen auf CO2. Methan steht dem in keiner Weise nach, da es als besonders aggressives Treibhausgas bekannt ist. Die Wirkung ist 25-mal stärker wie bei CO2.<br><br>\r\n          <b> Verbrennung von fossilen Brennstoffen</b><br>\r\nEin erhebliche Rolle bei der Verursachung des Klimawandels kommt der Verbrennung von fossilen Brennstoffen zu, wodurch nämlich eine Menge Treibhausgase, wie z.B. CO2 freigesetzt werden. Fossile Brennstoffe sind tote Biomasse, also Jahrmillionen alte Überreste von Tieren und Pflanzen, welche durch geologische Prozesse in Kohle, Erdgas und Erdöl umgewandelt wurde. Das Gegenstück zu den fossilen Brennstoffen sind die erneuerbaren Energien, näheres dazu kannst du in der Lektion \"Energien\" lernen.<br><br>\r\n\r\nDiese fossilen Brennstoffe werden noch in vielen Bereichen eingesetzt:\r\n<ul>\r\n<li>Verkehr und Reisen:<br>\r\nMobilität ist ein wichtiger Teil in unserem täglichen Leben. Geht es nun um das Reisen in die Ferien mit dem Flugzeug oder um den alltäglichen Transport mit dem Auto. Der Verkehr ist aber auch einer der grössten Verursacher von Treibhausgasen, da für deren Antrieb enorme Mengen an Treibstoffen, wie Benzin, Diesel oder Kerosin, benötigt und verbrannt werden.</li>\r\n \r\n<li>Industrie:<br>\r\nAuch die industrielle Produktion ist für den Anstieg der Treibhausgase mitverantwortlich. Vor allem die Kunststoffproduktion trägt dazu bei, den Kunststoffe bzw. Plastik und synthetische Fasern werden aus Öl und Gas gewonnen. Über 99% basieren auf fossilen Rohstoffen. Und die enorme Nachfrage nach Plastik lässt nicht nach, sie wächst stetig. Alleine in den letzten 20 Jahren hat sich die Produktion mehr als verdoppelt.</li>\r\n\r\n<li>Dabei spielt nicht nur die Herstellung eine tragende Rolle, sondern auch die Entsorgung. Bei der Verbrennung von Plastik entstehen weitere grosse Mengen an Treibhausgasen. Dies scheint noch gravierender, wenn man beachtet, dass es sich bei Plastik in den meisten Fällen nur um sehr kurzlebige Produkte handelt, die bereits nach einmaliger Benutzung wieder im Abfall landen.</li>\r\n\r\n<li>Der Einfluss von Plastik geht aber noch viel weiter. Leider wird viel Plastikabfall immer noch nicht richtig in Müllverbrennungsanlagen entsorgt und landet entweder an Land in der Natur oder vor allem auch in unseren Meeren. Jedes Jahr gelangen schätzungsweise 5 – 13 Millionen Tonnen Plastikmüll in die Meere. Eines der Probleme dabei: auch während des Zersetzungsprozesses von Plastik werden kontinuierlich Treibhausgase freigesetzt. Besonders viele Gase entstehen bei der Zersetzung in Salzwasser.</li>\r\n               </ul>               \r\n           <b>Auch die Landwirtschaft spielt eine tragende Rolle</b><br>\r\nDie Waldrodung gilt nach der Verbrennung von fossilen Brennstoffen als die wichtigste Quelle der CO2-Emissionen.<br><br>\r\n\r\nEigentlich nehmen Bäume und Pflanzen CO2 auf und wandeln dieses in Sauerstoff um. Doch bei der Abholzung von Wäldern wird das in den Bäumen gespeicherte CO2 freigesetzt. Der Schutz der Wälder ist also nicht nur wichtig für den Erhalt der Tier- und Pflanzenwelt, sondern auch für den Kampf gegen die globale Erderwärmung.<br><br>\r\n\r\nEin weiteres Problem stellt die zunehmende Viehzucht dar. Wiederkäuer (Rinder, Ziegen, Schafe) stossen bei der Verdauung grosse Mengen an Methan aus. Wir erinnern uns,: Methan ist ebenfalls eines der schädlichen Treibhausgase, mit einer noch viel stärkerer Auswirkung auf die Atmosphäre wie CO2.', '', 'assets/gif/klima_02.gif', 'assets/gif/klima_02.jpg', '', 0, 2, 'Die Treibhausgase mit der grössten Wirkung auf die Erderwärmung sind Kohlenstoffdioxid (CO2) und Methan, Fossile Brennstoffe sind Millionen Jahre alte Überreste von Tieren und Pflanzen. Diese wurden durch geologische Prozesse in Kohle sowie Erdgas und Erdöl umgewandelt, Eine tragende Rolle bei der Erderwärmung spielen die Verbrennung von fossilen Rohstoffen in der Industrie und im Verkehr,  ', 0, '', ''),
(20, 3, 'Folgen', '<b>Weitreichende Folgen</b><br>\r\nDer Klimawandel hat weitreichende Auswirkungen auf uns Menschen und unsere Umwelt. Jetzt schon spürbare Veränderungen werden sich in den kommenden Jahrzehnten voraussichtlich noch weiter verschärfen.<br><br>\r\nEinige der direkten Folgen sind:\r\n<ul>\r\n<li>Steigende Minimal- und Maximaltemperaturen</li>\r\n<li>Steigender Meeresspiegel und höhere Meerestemperaturen</li>\r\n<li>Extreme Wetterereignisse</li>\r\n<li>Gletscherschwund</li>\r\n</ul>\r\n\r\nDurch die steigenden Temperaturen schmelzen die Eisgebiete. In den letzten 30 Jahren hat sich die Eisfläche am Nordpol fast halbiert und wird immer dünner. Wenn die Erwärmung weiter so voranschreitet, könnte das Eis innerhalb der nächsten 20 Jahre ganz wegschmelzen. Auch die Gletscher in den Hochgebirgen verlieren immer mehr an Masse. Unter anderem wegen dieser Eisschmelze steigt der Meeresspiegel immer mehr an. Dadurch werden in der folgenden Zeit immer grössere Gebiete an den Küsten überschwemmt, Sturmfluten und Hochwasser drohen.<br><br>\r\n \r\nDank des Klimawandels steigt nicht nur der Meeresspiegel, sondern auch die Wassertemperatur der Meere. Dadurch verdunstet mehr Wasser und wird in der Luft als Wasserdampf gespeichert. Die Gefahr von grossen Unwettern, wie heftigen Regenfällen und Wirbelstürmen steigt. Der Klimawandel führt bei den Meeren aber nicht nur zu wärmen Wassertemperaturen, sondern auch zu einem höheren Säuregehalt. Wind und Wellen mischen das Kohlenstoffdioxid au der Luft mit dem Meerwasser. Es entsteht Kohlensäure, genau wie die, die du aus Mineralwasser oder Limonade kennst. Dieses saure Meerwasser wird immer mehr zur Bedrohung von kalkhaltigen Lebewesen, wie Korallen, Muscheln und Schnecken.<br><br>\r\n\r\nIn trockenen Regionen breiten sich durch die steigenden Temperaturen die Wüsten aus. Es kommt öfters zu immer längeren Dürreperioden, worauf die Gefahr droht, dass die Flüsse austrocknen und grüne Landesteile verdorren. Die Wasserknappheit wird sich weiter verstärken. Breitet sich die Dürre in den trockenen Regionen weiter aus, werden Waldbrände ebenfalls zur drängenden Gefahr.', '', 'assets/gif/klima_03.gif', 'assets/gif/klima_03.jpg', '', 0, 3, 'Die Folgen des Klimawandels ziehen sich weitreichend, Die Meeresspiegel steigen, Die Gletscher schmelzen, Dürreperioden kommen immer öfters vor, ', 0, '', ''),
(21, 3, 'Was kann ich dagegen tun?', 'Wir haben nun gelernt, dass der globale Klimawandel verheerende Folgen auf unsere Umwelt und unser gesamtes gesellschaftliches Zusammenleben hat. Wenn wir nichts gegen die Erwärmung unternehmen, wird es immer häufiger zu extremen Wetterereignissen und Naturkatastrophen kommen, nicht nur unser Lebensraum, auch diejenigen vieler bedrohter Tierarten werden zerstört und gesundheitliche Probleme werden steigen, wegen Trinkwasserknappheiten, Hungersnöten und Infektionskrankheiten. Es ist also jedem von uns ein daran gelegen, gemeinsam etwas zur Bekämpfung des Klimawandel zu leisten! Jeder kann seinen Teil dazu beitragen, auch im Alltag können kleine Dinge eine grosse Wirkung erzielen.<br><br>\r\n \r\nWas du tun kannst, um zu helfen:\r\n<ul>\r\n<li>Fortbewegung – öfter mit der ÖV, dem Fahrrad oder zu Fuss</li>\r\n<li>Einkaufen – möglichst auf Verpackungen, vor allem auf Plastik, verzichten, lieber immer denselben Stoffsack zum Einpacken mitnehmen, anstatt jedes Mal einen neuen Plastiksack zu verbrauchen und darauf achten, regional und Bio einzukaufen</li>\r\n<li>Essen – bewusster Umgang mit Fleisch und auch vegetarische/vegane Alternativen in Betracht ziehen und darauf achten, nicht unnötig Lebensmittel wegzuwerfen (mehr dazu findest du in der Lektion \"Foodwaste\")</li>\r\n<li>Energie – sparsamer und bewusster Umgang (mehr dazu findest du in der Lektion \"Energie\")</li>\r\n</ul>\r\n', '', 'assets/gif/klima_04.gif', 'assets/gif/klima_04.jpg', '', 0, 4, 'Wir können alle einen Einfluss nehmen und etwas gegen den Klimawandel unternehmen, Am wichtigsten ist der respektvolle Umgang mit unserer Umwelt; Sei es nun beim Sparen von Energie oder bei dem bewussten Konsum von Lebensmitteln und materiellen Dingen wie Kleidern,   ', 0, '', '');

